def urls_to_title(urls):
    titles = []
    for url in urls:
        # remove the url part we don't want
        url = str(url).replace("/posts/", "")

        # transform this-kind-of-string to this_kind_of_string
        url = inflection.underscore(url)

        # This Kind Of String
        titles.append(inflection.titleize(url))
    
    return titles


